let cookiesArr = [], cookie = '';
const notify = $.isNode() ? require('./sendNotify') : '';
const jdCookieNode = $.isNode() ? require('./jdCookie.js') : '';
if ($.isNode()) {
  Object.keys(jdCookieNode).forEach((item) => {
    cookiesArr.push(jdCookieNode[item])
  })
  if (process.env.JD_DEBUG && process.env.JD_DEBUG === 'false') console.log = () => {};
}
const fs = require('fs');
const JD_API_HOST = ' https://carnivalcity.m.jd.com';
!(async () => {
  if (!cookiesArr[0]) {
    $.msg($.name, '【提示】请先获取京东账号一cookie\n直接使用NobyDa的京东签到获取', 'https://bean.m.jd.com/', {"open-url": "https://bean.m.jd.com/"});
    return;
  }
  $.temp = [];
  for (let i = 0; i < cookiesArr.length; i++) {
    if (cookiesArr[i]) {
      cookie = cookiesArr[i];
      $.UserName = decodeURIComponent(cookie.match(/pt_pin=(.+?);/) && cookie.match(/pt_pin=(.+?);/)[1])
      $.index = i + 1;
      $.canHelp = true;
      await indexInfo();
      await supportList();
      await getHelp();
      // await shareUrl()
    }
  }
  console.log($.temp);
  await start();
})()
    .catch((e) => {
      $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
    })
    .finally(() => {
      $.done();
    })

async function start() {
  let oldData = $.temp;
  if (!fs.existsSync(`./shareCodes`)) fs.mkdirSync(`./shareCodes`);
  await fs.writeFileSync(`./shareCodes/jd_cityShareCodes.json`, JSON.stringify(oldData));
  // await fs.writeFileSync('jd_shareCodes.json', JSON.stringify(oldData));
  console.log('文件写入成功，新的shareCodes已经替换');
}
function indexInfo() {
  const options = taskUrl('/khc/index/indexInfo', { t: Date.now() })
  $.hotProductList = [];
  $.brandList = [];
  $.browseshopList = [];
  return new Promise( (resolve) => {
    $.get(options, async (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          data = JSON.parse(data);
          if (data.code === 200) {
            $.hotProductList = data['data']['hotProductList'];
            $.brandList = data['data']['brandList'];
            $.browseshopList = data['data']['browseshopList'];
            // console.log(`助力情况：${data['data']['supportedNums']}/${data['data']['supportNeedNums']}`);
            // if (data['data']['supportedNums'] >= data['data']['supportNeedNums']) {
            //   $.canHelp = false;//助力人数已满
            // }
          }
        }
      } catch (e) {
        $.logErr(e, resp);
      } finally {
        resolve();
      }
    })
  });
}
function supportList() {
  const options = taskUrl('/khc/index/supportList', { t: Date.now() })
  return new Promise( (resolve) => {
    $.get(options, async (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          data = JSON.parse(data);
          if (data.code === 200) {
            console.log(`助力情况：${data['data']['supportedNums']}/${data['data']['supportNeedNums']}`);
            if (data['data']['supportedNums'] >= data['data']['supportNeedNums']) {
              $.canHelp = false;//助力人数已满
            }
          }
        }
      } catch (e) {
        $.logErr(e, resp);
      } finally {
        resolve();
      }
    })
  });
}
function getHelp() {
  return new Promise(resolve => {
    const body = {
      t: Date.now()
    }
    const options = taskUrl("/khc/task/getSupport", body);
    $.get(options, async (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          data = JSON.parse(data);
          if (data.code === 200) {
            console.log(`\n您的助力码shareId(互助码每天都是变化的，旧的不可用)\n\n${data.data.shareId}\n`);
            if ($.canHelp && data.data.shareId) $.temp.push(data.data.shareId);
          } else {
            console.log(`获取邀请码失败：${JSON.stringify(data)}`);
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve(data);
      }
    })
  })
}

